mapper.select:
SELECT * FROM advert WHERE district in
'${district}'
AND price >= #{priceFrom} AND #{priceTo} >= price AND rooms in
${room}
AND #{timestamp} > publicationDate AND raw = FALSE ORDER BY publicationDate DESC limit #{limit}

mapper.update:
UPDATE advert_author
SET isMain = false
WHERE advertId = #{advertId};

INSERT INTO advert_author (isMain, advertId, userId)
VALUES (TRUE, #{advertId}, #{userId});
